import React from "react";
import { connect } from "react-redux";
import PageHeader from "../../components/PageHeader";
import axios from "axios";

class FileDocuments extends React.Component {
  state = {
    documents: [],
  };

  componentDidMount() {
    window.scrollTo(0, 0);
    this.fetchDocuments();
  }

  fetchDocuments = async () => {
    // Placeholder data, replace this fetch call with actual endpoint
    const response = await axios.get("http://127.0.0.1:5000/list-pdfs");
    if (response.status === 200) {
      const documents = response.data.categories.map((cat) => ({
        name: cat.category.replace(/([A-Z])/g, " $1").trim(), // Beautify the category name
        endpoint: `http://127.0.0.1:5000/${cat.category.toLowerCase()}`,
        pdfEndpoint: `http://127.0.0.1:5000/static/pdfs/${cat.files[0]}`, // assuming first file is relevant
      }));
      this.setState({ documents });
    } else {
      console.error("Failed to fetch documents:", response.status);
    }
  };

  downloadPdf = async (pdfUrl) => {
    const response = await axios({
      url: pdfUrl,
      method: "GET",
      responseType: "blob", // Important for creating a download
    });

    const downloadUrl = window.URL.createObjectURL(new Blob([response.data]));
    const link = document.createElement("a");
    link.href = downloadUrl;
    link.setAttribute("download", pdfUrl.split("/").pop());
    document.body.appendChild(link);
    link.click();
  };

  render() {
    return (
      <div
        style={{ flex: 1 }}
        onClick={() => {
          document.body.classList.remove("offcanvas-active");
        }}
      >
        <PageHeader
          HeaderText="File Documents"
          Breadcrumb={[
            { name: "File Manager", navigate: "" },
            { name: "File Documents", navigate: "" },
          ]}
        />
        <div className="container-fluid">
          <div className="row clearfix">
            {this.state.documents.map((doc, i) => (
              <div key={i} className="col-lg-4 col-md-6">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">{doc.name}</h5>
                    <a
                      href={doc.endpoint}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="btn btn-primary"
                    >
                      View Form
                    </a>
                    <button
                      onClick={() => this.downloadPdf(doc.pdfEndpoint)}
                      className="btn btn-secondary"
                    >
                      Download PDF
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = ({ ioTReducer }) => ({
  isSecuritySystem: ioTReducer.isSecuritySystem,
});

export default connect(mapStateToProps, {})(FileDocuments);
