import React, { useState, useRef } from "react";
import "./emergency_contacts_final.css";

function EmployeeEmergencyContacts() {
  const [formData, setFormData] = useState({
    employeeName: "",
    phoneNumber: "",
    address: "",
    cityStateZip: "",
    primaryName: "",
    primaryRelationship: "",
    primaryPhone: "",
    primaryAltPhone: "",
    secondaryName: "",
    secondaryRelationship: "",
    secondaryPhone: "",
    secondaryAltPhone: "",
    doctorName: "",
    doctorPhone: "",
    doctorAddress: "",
    dateSigned: "",
  });

  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({ ...prevState, [name]: value }));
  };

  const startDrawing = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
    const ctx = canvasRef.current.getContext("2d");
    ctx.beginPath();
    ctx.moveTo(offsetX, offsetY);
    setIsDrawing(true);
  };

  const draw = ({ nativeEvent }) => {
    if (!isDrawing) return;
    const { offsetX, offsetY } = nativeEvent;
    const ctx = canvasRef.current.getContext("2d");
    ctx.lineTo(offsetX, offsetY);
    ctx.stroke();
  };

  const stopDrawing = () => {
    const ctx = canvasRef.current.getContext("2d");
    ctx.closePath();
    setIsDrawing(false);
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Using the existing formData from state and adding the signature image
    const signatureImage = canvasRef.current.toDataURL("image/png");
    const completeFormData = {
      ...formData,
      signatureImage,
      dateSigned: formData.dateSigned || new Date().toISOString().split("T")[0], // Ensuring a date is always sent
    };

    try {
      const response = await fetch(
        "http://127.0.0.1:8000/submit_emergency_contact/",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(completeFormData),
        }
      );

      const result = await response.json();
      if (response.ok) {
        alert("Form submitted successfully!");
        console.log(result);
        // Optionally reset the form here
        setFormData({
          employeeName: "",
          phoneNumber: "",
          address: "",
          cityStateZip: "",
          primaryName: "",
          primaryRelationship: "",
          primaryPhone: "",
          primaryAltPhone: "",
          secondaryName: "",
          secondaryRelationship: "",
          secondaryPhone: "",
          secondaryAltPhone: "",
          doctorName: "",
          doctorPhone: "",
          doctorAddress: "",
          dateSigned: "",
        });
        clearCanvas(); // Clear the signature canvas
      } else {
        throw new Error(result.message || "An unknown error occurred");
      }
    } catch (error) {
      alert("Failed to submit form: " + error.message);
      console.error("Error submitting form: ", error);
    }
  };

  return (
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        padding: "20px",
        maxWidth: "800px",
        margin: "auto",
      }}
    >
      <form
        onSubmit={handleSubmit}
        style={{
          textAlign: "center",
          border: "2px solid #009688",
          padding: "20px",
        }}
      >
        <h2 style={{ color: "#009688" }}>Employee Emergency Contacts</h2>

        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            flexWrap: "wrap",
            marginBottom: "15px",
          }}
        >
          <div style={{ flexBasis: "48%", minWidth: "200px" }}>
            <label>Employee Name:</label>
            <input
              type="text"
              name="employeeName"
              value={formData.employeeName}
              onChange={handleInputChange}
              style={{
