import React from "react";
import { connect } from "react-redux";
import PageHeader from "../../components/PageHeader";
import axios from "axios";

class FileDocuments extends React.Component {
  state = {
    documents: [
      {
        name: "Application for Employment",
        endpoint: "/application_for_employment",
        pdfEndpoint: "/pdfs/Application_for_Employment.pdf",
      },
      {
        name: "Emergency Contacts",
        endpoint: "/emergency_contacts_final",
        pdfEndpoint: "/pdfs/Emergency_Contacts.pdf",
      },
      {
        name: "Anti Harassment Discrimination",
        endpoint: "/anti_harassment_discrimination_final",
        pdfEndpoint: "/pdfs/Anti_Harassment_Discrimination.pdf",
      },
      {
        name: "Employment Payroll",
        endpoint: "/submit_employment_payroll",
        pdfEndpoint: "/pdfs/Employment_Payroll.pdf",
      },
      {
        name: "Receipt of Company Property",
        endpoint: "/receipt_of_company_property",
        pdfEndpoint: "/pdfs/Receipt_of_Property.pdf",
      },
    ],
  };

  componentDidMount() {
    window.scrollTo(0, 0);
  }

  downloadPdf = async (endpoint) => {
    const response = await axios({
      url: endpoint,
      method: "GET",
      responseType: "blob", // Important
    });

    const url = window.URL.createObjectURL(new Blob([response.data]));
    const link = document.createElement("a");
    link.href = url;
    link.setAttribute("download", endpoint.split("/").pop()); //or any other extension
    document.body.appendChild(link);
    link.click();
  };

  render() {
    return (
      <div
        style={{ flex: 1 }}
        onClick={() => {
          document.body.classList.remove("offcanvas-active");
        }}
      >
        <PageHeader
          HeaderText="File Documents"
          Breadcrumb={[
            { name: "File Manager", navigate: "" },
            { name: "File Documents", navigate: "" },
          ]}
        />
        <div className="container-fluid">
          <div className="row clearfix">
            {this.state.documents.map((doc, i) => (
              <div key={i} className="col-lg-4 col-md-6">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">{doc.name}</h5>
                    <a
                      href={doc.endpoint}
                      target="_blank"
                      className="btn btn-primary"
                    >
                      View Form
                    </a>
                    <button
                      onClick={() => this.downloadPdf(doc.pdfEndpoint)}
                      className="btn btn-secondary"
                    >
                      Download PDF
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = ({ ioTReducer }) => ({
  isSecuritySystem: ioTReducer.isSecuritySystem,
});

export default connect(mapStateToProps, {})(FileDocuments);
