import React, { useState, useRef } from "react";
import "./DirectDepositForm.css"; // Ensure to create and import CSS for styling
import depositslip from "../../../../assets/images/deposit/direct_deposit.png";

function DirectDepositForm() {
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    cityStateZip: "",
    bankName: "",
    accountNumber: "",
    routingNumber: "",
    amount: "",
    percentage: "",
    accountType: "",
    companyName: "",
    date: "",
  });
  const signatureCanvas = useRef(null);
  const [signatureImage, setSignatureImage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const clearCanvas = () => {
    const canvas = signatureCanvas.current;
    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setSignatureImage("");
  };

  const handleCanvas = (e) => {
    const canvas = signatureCanvas.current;
    const ctx = canvas.getContext("2d");
    if (!painting) return;
    ctx.lineWidth = 2;
    ctx.lineCap = "round";
    ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
    ctx.stroke();
    ctx.beginPath();
    ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
  };

  let painting = false;

  const startPaint = (e) => {
    painting = true;
    handleCanvas(e);
  };

  const endPaint = () => {
    painting = false;
    const canvas = signatureCanvas.current;
    setSignatureImage(canvas.toDataURL("image/png"));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formSubmissionData = {
      ...formData,
      signatureImage,
    };

    try {
      const response = await fetch("/submit_direct_deposit/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formSubmissionData),
      });

      const data = await response.json();
      if (response.ok) {
        console.log("Form submitted successfully:", data);
        alert("Direct Deposit Authorization submitted successfully!");
      } else {
        throw new Error(data.message || "Failed to submit form");
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      alert(error.message);
    }
  };
  return (
    <div className="WordSection1">
      <h2 style={{ textAlign: "center" }}>DIRECT DEPOSIT AUTHORIZATION</h2>
      <p style={{ textAlign: "center" }}>
        Please complete ALL the information below.
      </p>
      <form onSubmit={handleSubmit}>
        <p>
          <strong>Name</strong>:{" "}
          <input
            type="text"
            name="name"
            className="input-line"
            onChange={handleChange}
            required
          />
        </p>
        <p>
          <strong>Full Address</strong>:{" "}
          <input
            type="text"
            name="address"
            className="input-line"
            onChange={handleChange}
            required
          />
        </p>
        {/* <p>
          <strong>City, State, Zip</strong>:{" "}
          <input
            type="text"
            name="cityStateZip"
            className="input-line"
            onChange={handleChange}
            required
          />
        </p> */}

        {/* Image and Table for the form */}
        <div>
          <img
            src={depositslip}
            alt="Direct Deposit Image"
            style={{ height: "150px", margin: "70px", alignItems: "center" }}
          />
        </div>
        {/* The rest of the form */}
        <canvas
          ref={signatureCanvas}
          onMouseDown={startPaint}
          onMouseUp={endPaint}
          onMouseMove={handleCanvas}
          onMouseLeave={endPaint}
        />
        <button class="button" type="button" onClick={clearCanvas}>
          Clear Signature
        </button>
        <input type="hidden" value={signatureImage} />
        <br></br>
        <br></br>
        <p style={{ textAlign: "center" }}>
          Date:<br></br>
        </p>
        <input
          style={{ alignSelf: "center" }}
          type="date"
          name="date"
          onChange={handleChange}
          required
        />
        <br></br>
        <br></br>
        <input type="submit" value="Submit Authorization" />
      </form>
    </div>
  );
}

export default DirectDepositForm;
