import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import PageHeader from "../../components/PageHeader";
import {
  Button,
  Typography,
  IconButton,
  Tooltip,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from "@mui/material";
import FileDownloadIcon from "@mui/icons-material/FileDownload";
import VisibilityIcon from "@mui/icons-material/Visibility";
import DeleteIcon from "@mui/icons-material/Delete";
import Slider from "react-slick";
import "./media.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

function FileMedia({ isSecuritySystem }) {
  const [directories, setDirectories] = useState([]);
  const [searchType, setSearchType] = useState("");
  const [searchParams, setSearchParams] = useState({});
  const [searchResults, setSearchResults] = useState([]);

  // Define the slider settings
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          initialSlide: 1,
        },
      },
    ],
  };

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/list-pdfs`)
      .then((response) => response.json())
      .then((data) => {
        if (data.categories) {
          setDirectories(data.categories);
        }
      })
      .catch((error) => console.error("Error fetching PDFs:", error));
  }, []);

  // Define the handleDelete function
  const handleDelete = (category, filename) => {
    if (!window.confirm(`Are you sure you want to delete ${filename}?`)) {
      return;
    }
    const url = `${process.env.REACT_APP_API_URL}/${category}/${filename}`;
    fetch(url, { method: "DELETE" })
      .then((response) => response.json())
      .then((data) => {
        if (data.message) {
          alert(data.message);
          setDirectories((prev) =>
            prev.map((dir) =>
              dir.category === category
                ? {
                    ...dir,
                    files: dir.files.filter((file) => file !== filename),
                  }
                : dir
            )
          );
        } else if (data.error) {
          alert(data.error);
        }
      })
      .catch((error) => {
        console.error("Error deleting the file:", error);
        alert("Failed to delete the file");
      });
  };

  const handleInputChange = (e) => {
    setSearchParams({ ...searchParams, [e.target.name]: e.target.value });
  };

  const handleSearch = async () => {
    let url;
    switch (searchType) {
      case "anti_harassment":
        url = `${process.env.REACT_APP_API_URL}/anti_harassment_submit_form_search?printed_name=${searchParams.printed_name}&date_of_birth=${searchParams.date_of_birth}`;
        break;
      case "applications":
        url = `${process.env.REACT_APP_API_URL}/search_applications?position=${searchParams.position}&name=${searchParams.name}&date_of_birth=${searchParams.date_of_birth}`;
        break;
      case "cell_phone":
        url = `${process.env.REACT_APP_API_URL}/search_cell_phone_policy_records?printed_name=${searchParams.printed_name}&date_of_birth=${searchParams.date_of_birth}`;
        break;
      case "direct_deposits":
        url = `${process.env.REACT_APP_API_URL}/search_direct_deposits?name=${searchParams.name}&date=${searchParams.date}`;
        break;
      case "emergency_contacts":
        url = `${process.env.REACT_APP_API_URL}/search_emergency_contacts?name=${searchParams.name}&date=${searchParams.date}`;
        break;
      default:
        return;
    }

    try {
      const response = await fetch(url);
      const data = await response.json();
      setSearchResults(data);
    } catch (error) {
      console.error("Search failed:", error);
      alert("Failed to fetch search results.");
    }
  };

  return (
    <div className="file-media">
      <PageHeader title="File Media Management" />
      <FormControl fullWidth>
        <InputLabel id="search-type-label">Search Type</InputLabel>
        <Select
          labelId="search-type-label"
          value={searchType}
          label="Search Type"
          onChange={(e) => setSearchType(e.target.value)}
        >
          <MenuItem value="anti_harassment">
            Anti Harassment Form Search
          </MenuItem>
          <MenuItem value="applications">Applications Search</MenuItem>
          <MenuItem value="cell_phone">
            Cell Phone Policy Records Search
          </MenuItem>
          <MenuItem value="direct_deposits">Direct Deposits Search</MenuItem>
          <MenuItem value="emergency_contacts">
            Emergency Contacts Search
          </MenuItem>
        </Select>
      </FormControl>
      {searchType && (
        <div>
          {Object.entries(
            {
              anti_harassment: [
                ["printed_name", "Printed Name"],
                ["date_of_birth", "Date of Birth"],
              ],
              applications: [
                ["position", "Position"],
                ["name", "Name"],
                ["date_of_birth", "Date of Birth"],
              ],
              cell_phone: [
                ["printed_name", "Printed Name"],
                ["date_of_birth", "Date of Birth"],
              ],
              direct_deposits: [
                ["name", "Name"],
                ["date", "Date"],
              ],
              emergency_contacts: [
                ["name", "Name"],
                ["date", "Date"],
              ],
            }[searchType]
          ).map(([key, label]) => (
            <TextField
              key={key}
              label={label}
              name={key}
              value={searchParams[key] || ""}
              onChange={handleInputChange}
              fullWidth
              margin="normal"
            />
          ))}
          <Button onClick={handleSearch} variant="contained" color="primary">
            Search
          </Button>
        </div>
      )}
      <br />
      <div>
        {searchResults.map((result, index) => (
          <Typography key={index}>{JSON.stringify(result)}</Typography>
        ))}
      </div>
      <br />
      {directories.map((dir) => (
        <div key={dir.category} className="category">
          <Typography variant="h5" component="h2" gutterBottom>
            {dir.category}
          </Typography>
          <Slider {...settings}>
            {dir.files.map((file, index) => (
              <div key={index} className="document-card">
                <div className="file-actions">
                  <Tooltip title="View">
                    <IconButton
                      href={`${process.env.REACT_APP_API_URL}/${dir.category}/${file}`}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      <VisibilityIcon />
                    </IconButton>
                  </Tooltip>
                  <Tooltip title="Download">
                    <IconButton
                      href={`${process.env.REACT_APP_API_URL}/${dir.category}/${file}`}
                      download
                    >
                      <FileDownloadIcon />
                    </IconButton>
                  </Tooltip>
                  <Tooltip title="Delete">
                    <IconButton
                      color="error"
                      onClick={() => handleDelete(dir.category, file)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </Tooltip>
                </div>
                <Typography variant="caption" display="block" gutterBottom>
                  {file}
                </Typography>
              </div>
            ))}
          </Slider>
        </div>
      ))}
    </div>
  );
}

export default connect()(FileMedia);
