import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import PageHeader from "../../components/PageHeader";

function FileMedia({ isSecuritySystem }) {
  const [directories, setDirectories] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/list-pdfs")
      .then((response) => response.json())
      .then((data) => {
        if (data.categories) {
          setDirectories(data.categories);
        }
      })
      .catch((error) => console.error("Error fetching PDFs:", error));
  }, []);

  return (
    <div onClick={() => document.body.classList.remove("offcanvas-active")}>
      <PageHeader
        HeaderText="File Media"
        Breadcrumb={[
          { name: "File Manager", navigate: "" },
          { name: "File Media", navigate: "" },
        ]}
      />
      <div className="container-fluid">
        {directories.map((dir) => (
          <div key={dir.category}>
            <h3>{dir.category.replace(/_/g, " ").toUpperCase()}</h3>
            <div
              style={{
                display: "flex",
                overflowX: "scroll",
                marginBottom: "20px",
              }}
            >
              {dir.files.map((file, index) => (
                <div
                  key={index}
                  style={{ marginRight: "10px", minWidth: "200px" }}
                >
                  <div style={{ marginBottom: "5px" }}>
                    <strong>{file}</strong>
                  </div>
                  <a
                    href={`http://localhost:5000/${dir.category}/${file}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <button style={{ marginRight: "5px" }}>View</button>
                  </a>
                  <a
                    href={`http://localhost:5000/${dir.category}/${file}`}
                    download
                  >
                    <button>Download</button>
                  </a>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const mapStateToProps = ({ ioTReducer }) => ({
  isSecuritySystem: ioTReducer.isSecuritySystem,
});

export default connect(mapStateToProps)(FileMedia);
