import React, { useState } from "react";
import defaultPDF from "../../../../assets/files/i-9.pdf"; // Change i9 to defaultPDF for clarity

export default function PDFViewer() {
  const [file, setFile] = useState(defaultPDF); // Set initial state to the imported PDF

  const onFileChange = (event) => {
    // Create URL for uploaded file and update state
    const uploadedFile = event.target.files[0];
    if (uploadedFile) {
      setFile(URL.createObjectURL(uploadedFile));
    }
  };

  const onFormSubmit = (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    formData.append("pdf_file", file); // Make sure to append the actual file not the URL

    fetch("http://127.0.0.1:8000/upload_pdf/", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => console.log(data));
  };

  return (
    <div>
      <input type="file" onChange={onFileChange} accept="application/pdf" />
      <iframe src={file} width="100%" height="500px"></iframe>
      <form onSubmit={onFormSubmit} enctype="multipart/form-data">
        <input name="full_name" placeholder="Full Name" required />
        <input type="date" name="date_of_birth" required />
        <input
          type="hidden"
          name="submitted_year"
          value={new Date().getFullYear()}
        />
        <button type="submit">Save PDF</button>
      </form>
    </div>
  );
}
