import React, { useState, useEffect } from "react";
import { PDFDocument } from "pdf-lib";
import I9 from "../../../../assets/files/i-9.pdf";

export default function PDFViewer() {
  const [pdfUrl, setPdfUrl] = useState(null);

  useEffect(() => {
    // Fetch the initial PDF from a public URL and set it as a blob
    fetch(I9)
      .then((response) => response.blob())
      .then((blob) => {
        const url = URL.createObjectURL(blob);
        setPdfUrl(url);
      });
  }, []);

  const onFileChange = async (event) => {
    const uploadedFile = event.target.files[0];
    if (uploadedFile) {
      const pdfDoc = await PDFDocument.load(await uploadedFile.arrayBuffer());
      // Perform pdf-lib operations here
      const pdfBytes = await pdfDoc.save();
      const blob = new Blob([pdfBytes], { type: "application/pdf" });
      const url = URL.createObjectURL(blob);
      setPdfUrl(url); // Update the iframe src with the new PDF
    }
  };

  const onFormSubmit = (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append("full_name", event.target.full_name.value);
    formData.append("date_of_birth", event.target.date_of_birth.value);
    formData.append("submitted_year", new Date().getFullYear());
    formData.append(
      "pdf_file",
      new Blob([pdfBytes], { type: "application/pdf" }),
      "edited.pdf"
    );
    // Confirm that you want to send the URL or the blob

    fetch("http://127.0.0.1:8000/upload_pdf/", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => console.log(data));
  };

  return (
    <div>
      <input type="file" onChange={onFileChange} accept="application/pdf" />
      {pdfUrl ? (
        <iframe src={pdfUrl} width="100%" height="500px"></iframe>
      ) : (
        <p>Loading PDF...</p>
      )}
      <form onSubmit={onFormSubmit} enctype="multipart/form-data">
        <input name="full_name" placeholder="Full Name" required />
        <input type="date" name="date_of_birth" required />
        <button type="submit">Save PDF</button>
      </form>
    </div>
  );
}
