          <canvas
            ref={canvasRef}
            width="300"
            height="100"
            style={{
              border: "1px solid #cccccc",
              alignContent: "center",
              alignSelf: "center",
              color: "black",
            }}
            onMouseDown={(e) => {
              const ctx = canvasRef.current.getContext("2d");
              ctx.isDrawing = true;
              ctx.beginPath();
              ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
            }}
            onMouseMove={(e) => {
              const ctx = canvasRef.current.getContext("2d");
              if (ctx.isDrawing) {
                ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
                ctx.stroke();
              }
            }}
            onMouseUp={() => {
              const ctx = canvasRef.current.getContext("2d");
              ctx.isDrawing = false;
              setSignatureImageData(canvasRef.current.toDataURL("image/png"));
            }}
            onMouseOut={() => {
              const ctx = canvasRef.current.getContext("2d");
              ctx.isDrawing = false;
            }}
          />
          <br />
          <button type="button" onClick={clearCanvas}>
            Clear Signature
          </button>
          <br />
          <label htmlFor="date" style={{ alignSelf: "center" }}>
            Date:
          </label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
          <br />
          <label htmlFor="printedName" style={{ alignSelf: "center" }}>
            Printed Name:
          </label>
          <input
            id="printedName"
            type="text"
            value={printedName}
            onChange={(e) => setPrintedName(e.target.value)}
          />
          <br />
          <button type="submit">Acknowledge</button>
        </div>
