import React, { useState, useEffect } from "react";
import { PDFDocument, error } from "pdf-lib";
import I9 from "../../../../assets/files/i-9.pdf";

export default function PDFViewer() {
  const [pdfUrl, setPdfUrl] = useState(null);
  const [pdfBytes, setPdfBytes] = useState(null); // Store the PDF bytes for submission

  useEffect(() => {
    fetch(I9)
      .then((response) => {
        if (response.ok) return response.blob();
        throw new Error("Failed to load the initial PDF");
      })
      .then((blob) => {
        const url = URL.createObjectURL(blob);
        setPdfUrl(url);
      })
      .catch((error) => console.error("Error loading PDF:", error));
  }, []);

  const onFileChange = async (event) => {
    const uploadedFile = event.target.files[0];
    if (uploadedFile) {
      const pdfDoc = await PDFDocument.load(await uploadedFile.arrayBuffer());
      console.log("Original PDF loaded");

      // Simulate an editing operation
      // For example, add a text to the first page (you must adjust according to your PDF structure)
      const pages = pdfDoc.getPages();
      const firstPage = pages[0];
      const { width, height } = firstPage.getSize();
      firstPage.drawText("Hello, this is a test edit!", {
        x: 50,
        y: height - 100,
        size: 25,
      });

      console.log("PDF edited");

      const pdfBytes = await pdfDoc.save();
      console.log("PDF saved to bytes");

      const blob = new Blob([pdfBytes], { type: "application/pdf" });
      const url = URL.createObjectURL(blob);
      setPdfUrl(url);
      setPdfBytes(pdfBytes); // Save the PDF bytes for later submission
      console.log("PDF URL set for display");
    }
  };

  const onFormSubmit = (event) => {
    event.preventDefault();
    if (!pdfBytes) {
      console.error(error);
      return;
    }
    const formData = new FormData();
    formData.append("full_name", event.target.full_name.value);
    formData.append("date_of_birth", event.target.date_of_birth.value);
    formData.append("submitted_year", new Date().getFullYear());
    formData.append(
      "pdf_file",
      new Blob([pdfBytes], { type: "application/pdf" }),
      "edited.pdf"
    );

    console.log("Submitting form");

    fetch("http://127.0.0.1:8000/upload_pdf/", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Submission response:", data);
        alert("PDF submitted successfully!");
      })
      .catch((error) => {
        console.error("Error submitting PDF:", error);
      });
  };

  return (
    <div>
      <input type="file" onChange={onFileChange} accept="application/pdf" />
      {pdfUrl ? (
        <iframe
          src={pdfUrl}
          style={{ width: "100%", height: "500px", border: "none" }}
        />
      ) : (
        <p>Loading PDF...</p>
      )}
      <form onSubmit={onFormSubmit} enctype="multipart/form-data">
        <input name="full_name" placeholder="Full Name" required />
        <input type="date" name="date_of_birth" required />
        <button type="submit">Save PDF</button>
      </form>
    </div>
  );
}
