import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import PageHeader from "../../components/PageHeader";
import FileDocumentCard from "../../components/FileManager/FileDocumentCard";

function FileMedia({ isSecuritySystem }) {
  const [directories, setDirectories] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/list-pdfs")
      .then((response) => response.json())
      .then((data) => {
        if (data.categories) {
          setDirectories(data.categories);
        }
      })
      .catch((error) => console.error("Error fetching PDFs:", error));
  }, []);

  return (
    <div onClick={() => document.body.classList.remove("offcanvas-active")}>
      <PageHeader
        HeaderText="File Media"
        Breadcrumb={[
          { name: "File Manager", navigate: "" },
          { name: "File Media", navigate: "" },
        ]}
      />
      <div className="container-fluid">
        {directories.map((dir) => (
          <div key={dir.category}>
            <h3>{dir.category.replace(/([A-Z])/g, " $1").trim()}</h3>
            <div
              style={{
                display: "flex",
                overflowX: "scroll",
                marginBottom: "20px",
              }}
            >
              {dir.files.map((file, index) => (
                <a
                  key={index}
                  href={`http://localhost:5000/static/pdfs/${file}`}
                  style={{ marginRight: "10px" }}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <FileDocumentCard
                    DocumentIconClass="fa fa-file-pdf-o"
                    DocumentName={file}
                    DocumentSize="Unknown" // Size not provided by API
                    DocumentDate="Unknown" // Date not provided by API
                  />
                </a>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const mapStateToProps = ({ ioTReducer }) => ({
  isSecuritySystem: ioTReducer.isSecuritySystem,
});

export default connect(mapStateToProps)(FileMedia);
